// === Capture Article Content ===
const inputData = $input.item.json;
let articleContent = inputData.message?.content
    || inputData.text
    || inputData.content
    || inputData.article_content
    || '';

if (typeof articleContent === 'object') {
    articleContent = JSON.stringify(articleContent);
}

// Fallback: try upstream nodes
if (!articleContent || articleContent.length < 100) {
    const tryNodes = [
        'Document Export Sanitization3', 'Document Export Sanitization4',
        'Document Export Sanitization6', 'Document Export Sanitization7',
        'Document Export Sanitization',
        '1st Improvement LLM2', '1st Improvement LLM3',
        'Improvement LLM2', 'Improvement LLM3',
        'Claude Humanised Readability Rewrite'
    ];
    for (const nodeName of tryNodes) {
        try {
            const data = $node[nodeName]?.json;
            if (data) {
                const text = data.message?.content || data.text || data.content || '';
                if (text && typeof text === 'string' && text.length > (articleContent?.length || 0)) {
                    articleContent = text;
                }
            }
        } catch(e) {}
    }
}

// === Capture Scoring Data from Scoring Agents ===
let scoringData = null;

// Try each scoring agent in order (most recent first)
const scoringNodes = [
    '2nd Scoring Agent2', '2nd Scoring Agent3',
    '1st Scoring Agent2', '1st Scoring Agent3',
    'Scoring7', 'Scoring1'
];

for (const scoringNode of scoringNodes) {
    try {
        const data = $node[scoringNode]?.json;
        const content = data?.message?.content;
        if (content) {
            // content could be JSON object or JSON string
            scoringData = typeof content === 'string' ? JSON.parse(content) : content;
            if (scoringData?.overallScore !== undefined) break;
        }
    } catch(e) {}
}

// Build structured audit_data
const requestId = $('Webhook1').item.json.body.request_id;
const title = $('Webhook1').item.json.body.title || 'Test Article';
const wordCount = articleContent ? articleContent.split(/\s+/).filter(Boolean).length : 0;

const auditData = scoringData ? {
    // 6 category scores
    seoOptimization: scoringData.seoOptimization || 0,
    humanAuthenticity: scoringData.humanAuthenticity || 0,
    readabilityStructure: scoringData.readabilityStructure || 0,
    engagementClarity: scoringData.engagementClarity || 0,
    trustAuthority: scoringData.trustAuthority || 0,
    conversionValue: scoringData.conversionValue || 0,
    // Overall
    overallScore: scoringData.overallScore || 0,
    alignment_score: scoringData.overallScore || 0,
    // Text fields
    issues: scoringData.issues || '',
    fixes: scoringData.fixes || '',
    impact: scoringData.impact || '',
    // Meta
    alignment_summary: `Content scored ${scoringData.overallScore}/100 across 6 quality dimensions`,
    passed: (scoringData.overallScore || 0) >= 80,
    word_count: wordCount,
    title: title
} : {
    alignment_score: 0,
    alignment_summary: `Test completed - ${wordCount} words generated for "${title}"`,
    passed: false,
    word_count: wordCount,
    title: title,
    seoOptimization: 0, humanAuthenticity: 0, readabilityStructure: 0,
    engagementClarity: 0, trustAuthority: 0, conversionValue: 0,
    overallScore: 0, issues: 'Scoring data not available', fixes: '', impact: ''
};

// === Send Callback ===
console.log('Sending callback for request:', requestId);
console.log('Overall Score:', auditData.overallScore);
console.log('Content Length:', articleContent.length);

try {
    const response = await this.helpers.httpRequest({
        method: 'POST',
        url: 'https://skywide-content-flow.vercel.app/api/test-callback',
        body: {
            request_id: requestId,
            status: 'completed',
            audit_data: auditData,
            content_markdown: articleContent
        },
        headers: { 'Content-Type': 'application/json' }
    });

    return [{ json: {
        success: true,
        content_length: articleContent.length,
        word_count: wordCount,
        overall_score: auditData.overallScore,
        scoring_source: scoringData ? 'real' : 'fallback',
        api_response: response
    }}];
} catch (error) {
    console.error('Callback failed:', error.message);
    if (error.response) {
        console.error('Response data:', JSON.stringify(error.response.data));
        console.error('Response status:', error.response.status);
    }
    throw error;
}

// Keyword Strategist Node Logic
// This code is designed to run inside an n8n Code Node.

const inputData = items[0].json.body || items[0].json;

// Extract raw keyword string (assuming comma-separated)
const rawKeywords = inputData.primary_keywords || "";
const keywordsList = rawKeywords.split(',').map(k => k.trim()).filter(k => k.length > 0);

// RULES:
// 1. Primary Keyword: The first one provided (or explicitly marked).
// 2. Secondary Keywords: All subsequent keywords.
// 3. Billy's Rule: Primary must be in first 50 words.
// 4. Billy's Rule: Primary must be in at least one H2.
// 5. Billy's Rule: Secondary keywords naturally distributed.

let primaryKeyword = keywordsList.length > 0 ? keywordsList[0] : "";
let secondaryKeywords = keywordsList.slice(1);

// Generate Placement Map
const placementMap = {
    primary: {
        term: primaryKeyword,
        required_placements: [
            "H1 Title",
            "First 50 Words (Introduction)",
            "At least one H2 Subheading",
            "Final Paragraph (Conclusion)"
        ],
        density_target: "1.5%",
        voice_anchor: "Use with 'In our experience' or 'From the field'"
    },
    secondary: secondaryKeywords.map(k => ({
        term: k,
        required_placements: ["Body Paragraph", "H3 (Optional)"],
        voice_anchor: "Contextual mention"
    })),
    constraints: {
        banned_phrases: [
            "Additionally",
            "Let's dive in",
            "In conclusion",
            "The bottom line",
            "Moreover",
            "Furthermore"
        ],
        paragraph_rule: "Max 3 sentences. No two consecutive paragraphs starting with the same word.",
        voice_rule: "Write as 'We' (the contractor). Use 'You' for the reader."
    }
};

// Construct System Prompt Injection
// This string will be injected into the LLM's system prompt to enforce rules.
const systemPromptInjection = `
### KEYWORD ORCHESTRATION INSTRUCTIONS
You must strictly follow this placement map for SEO optimization while maintaining Contractor Voice.

1. **PRIMARY KEYWORD:** "${primaryKeyword}"
   - **MANDATORY:** Must appear in the **first 50 words** of the intro.
   - **MANDATORY:** Must appear in at least one **H2 subheading**.
   - **MANDATORY:** Must appear in the **final paragraph**.
   - **Constraint:** Do not just insert it. Anchor it with authority: *"In our experience with ${primaryKeyword}..."* or *"When we handle ${primaryKeyword}..."*

2. **SECONDARY KEYWORDS:**
   ${secondaryKeywords.map(k => `- "${k}" (Distribute naturally in body)`).join('\n   ')}

3. **EDITORIAL VOICE Rules (Billy's List):**
   - **FORBIDDEN:** Do not use "Additionally", "Let's dive in", "In conclusion", or "The bottom line".
   - **VOICE:** Use "We" (contractor perspective).
   - **LENGTH:** Max 3 sentences per paragraph.
`;

return {
    json: {
        ...inputData,
        keyword_strategy: {
            primary: primaryKeyword,
            secondary: secondaryKeywords,
            map: placementMap,
            system_prompt_injection: systemPromptInjection
        }
    }
};
